mapper.select:
select count(1) as successNums,sum(money) as total from
withdraw_cash where user_type = #{userType,jdbcType=TINYINT} and
status = #{status,jdbcType=TINYINT}

update.foreach:
update withdraw_cash set status = #{item.status,jdbcType=TINYINT}, audit_faild_reason = #{item.auditFailReason,jdbcType=VARCHAR}, audit_user_id = #{item.auditUserId,jdbcType=BIGINT}, audit_user_name = #{item.auditUserName,jdbcType=VARCHAR}, gmt_modified = #{item.gmtModified,jdbcType=TIMESTAMP},
gmt_accept = #{item.gmtAccept,jdbcType=TIMESTAMP}
gmt_finish = #{item.gmtFinish,jdbcType=TIMESTAMP}
where id = #{item.id,jdbcType=BIGINT}
