select id as
memberId,num,account,region_path,region_name,nickname,headimg,level
from member where id not in(select member_id from fans_merchant where merchant_id = #{merchantId,jdbcType=BIGINT})

	and region_path like CONCAT(#{regionPath,jdbcType=VARCHAR},'%')


	and sex = #{sex,jdbcType=TINYINT}


	and TIMESTAMPDIFF(YEAR,birthday,CURRENT_DATE) >=
	#{startAge,jdbcType=INTEGER} and
	TIMESTAMPDIFF(YEAR,birthday,CURRENT_DATE) <=
	#{endAge,jdbcType=INTEGER}


	limit #{inviteCount,jdbcType=INTEGER}


	limit #{offset,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}

select count(1)
from member where id not in(select member_id from fans_merchant where merchant_id = #{merchantId,jdbcType=BIGINT})

	and region_path like CONCAT(#{regionPath,jdbcType=VARCHAR},'%')


	and sex = #{sex,jdbcType=TINYINT}


	and TIMESTAMPDIFF(YEAR,birthday,CURRENT_DATE) >=
	#{startAge,jdbcType=INTEGER} and
	TIMESTAMPDIFF(YEAR,birthday,CURRENT_DATE) <=
	#{endAge,jdbcType=INTEGER}

select mem.account,mem.region_path,mem.region_name,mem.nickname,mem.headimg,mem.level,fm.gmt_create from fans_merchant fm inner join member mem on fm.member_id = mem.id where fm.merchant_id = #{merchantId,jdbcType=BIGINT} and fm.`status` = 1
and mem.region_path like CONCAT(#{regionPath,jdbcType=VARCHAR},'%')
order by fm.id desc limit #{offset,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}
